import React from 'react';
import { useChat } from '../../chat/context/ChatContext';
import { useAuth } from '../../auth/context/AuthContext';
import ChatHeader from './ChatHeader';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import '../css/ChatWindow.css';

const ChatWindow = ({ contact, isDarkTheme }) => {
  const { user } = useAuth();
  const { messages, groupMessages, typing } = useChat();

  if (!contact) {
    return (
      <div className="chat-window">
        <div className="empty-state">
          <div className="empty-icon">
            <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" />
              <path d="M13 8H7" />
              <path d="M17 12H7" />
            </svg>
          </div>
          <h3>Welcome to Ketaharu!</h3>
          <p>
            {contact?.type === 'group' 'Select a group to start collaborating with your team'
              : 'Select a friend to start chatting'
            }
          </p>
        </div>
      </div>
    );
  }

  // Get messages for the selected contact
  const getCurrentChatMessages = () => {
    if (!contact || !user) return [];

    if (contact.type === 'group') {
      return groupMessages[contact.groupId] || [];
    } else {
      const chatId = [user.uid, contact.uid].sort().join('_');
      return messages[chatId] || [];
    }
  };

  const currentMessages = getCurrentChatMessages();
  const isTyping = contact && typing[contact.type === 'group' contact.groupId : contact.uid];

  return (
    <div className="chat-window">
      <ChatHeader contact={contact} isDarkTheme={isDarkTheme} />

      <div className="chat-messages">
        {currentMessages.length > 0 (
          <MessageList 
            messages={currentMessages} 
            contact={contact}
            isDarkTheme={isDarkTheme}
          />
        ) : (
          <div className="empty-messages">
            <div className="empty-icon">
              <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" />
                <path d="M13 8H7" />
                <path d="M17 12H7" />
              </svg>
            </div>
            <h3>No messages yet</h3>
            <p>
              {contact.type === 'group' 'Start the conversation in your group!'
                : 'Start the conversation with your friend!'
              }
            </p>
          </div>
        )}
      </div>

      {isTyping && (
        <div className="typing-indicator">
          <div className="typing-text">
            {contact.type === 'group' 'Someone is typing...' : `${contact.displayName} is typing...`}
          </div>
          <div className="typing-dots">
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      )}

      <MessageInput 
        contact={contact}
        isDarkTheme={isDarkTheme}
      />
    </div>
  );
};

export default ChatWindow;

import React, { useState } from 'react';
import { useChat } from '../../chat/context/ChatContext';
import '../css/ContactItem.css';

const ContactItem = ({ contact, isSelected, onClick, type = 'friend' }) => {
  const { removeFriend, messages } = useChat();
  const [showOptions, setShowOptions] = useState(false);

  const handleRemoveFriend = async () => {
    if (type === 'friend') {
      await removeFriend(contact.uid);
    }
    setShowOptions(false);
  };

  // Get last message for this friend
  const chatId = contact.uid [contact.uid, contact.uid].sort().join('_') : null;
  const friendMessages = messages[chatId] || [];
  const lastMessage = friendMessages[friendMessages.length - 1];

  return (
    <div 
      className={`contact-item ${isSelected 'selected' : ''}`}
      onClick={onClick}
    >
      <div className="contact-avatar">
        <img src={contact.avatar || `https://api.dicebear.com/7.x/avataaars/svg?seed=${contact.displayName}`} alt={contact.displayName} />
        <div className={`status-dot ${contact.online 'online' : 'offline'}`}></div>
      </div>

      <div className="contact-info">
        <div className="contact-name">{contact.displayName}</div>
        {lastMessage && (
          <div className="contact-last-message">
            {lastMessage.sender === contact.uid lastMessage.text : `You: ${lastMessage.text}`}
          </div>
        )}
        {lastMessage && (
          <div className="contact-time">
            {new Date(lastMessage.timestamp).toLocaleTimeString([], { 
              hour: '2-digit', 
              minute: '2-digit' 
            })}
          </div>
        )}
      </div>

      {type === 'friend' && (
        <div className="contact-actions">
          <button 
            className="options-button"
            onClick={(e) => {
              e.stopPropagation();
              setShowOptions(!showOptions);
            }}
            aria-label="More options"
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <circle cx="12" cy="12" r="1"></circle>
              <circle cx="19" cy="12" r="1"></circle>
              <circle cx="5" cy="12" r="1"></circle>
            </svg>
          </button>

          {showOptions && (
            <div className="options-menu" onClick={(e) => e.stopPropagation()}>
              <button 
                className="option-item remove-friend"
                onClick={handleRemoveFriend}
              >
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M18 6L6 18M6 6l12 12"></path>
                </svg>
                Remove Friend
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ContactItem;

import React, { useState, useEffect } from 'react';
import { useAuth } from '../../auth/context/AuthContext';
import { useChat } from '../../chat/context/ChatContext';
import Sidebar from './Sidebar';
import ChatWindow from './ChatWindow';
import ProfileModal from './ProfileModal';
import '../css/Dashboard.css';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const { selectedFriend, selectedGroup } = useChat();
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [isMobileSidebarOpen, setIsMobileSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Debug log for modal state
  console.log('Dashboard render:', { showProfileModal, user: user?.displayName });

  const handleToggleTheme = () => {
    const newTheme = !isDarkTheme;
    setIsDarkTheme(newTheme);

    if (newTheme) {
      document.body.classList.add('dark-theme');
    } else {
      document.body.classList.remove('dark-theme');
    }
  };

  const handleToggleSidebar = () => {
    // On mobile, toggle the mobile sidebar
    if (isMobile) {
      setIsMobileSidebarOpen(!isMobileSidebarOpen);
    } else {
      // On desktop, toggle the collapsed state
      setSidebarCollapsed(!sidebarCollapsed);
    }
  };

  const handleCloseMobileSidebar = () => {
    setIsMobileSidebarOpen(false);
  };

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const getSelectedContact = () => {
    if (selectedFriend) {
      return { ...selectedFriend, type: 'friend' };
    }
    if (selectedGroup) {
      return { ...selectedGroup, type: 'group' };
    }
    return null;
  };

  const selectedContact = getSelectedContact();

  return (
    <div className={`dashboard ${isDarkTheme 'dark' : ''}`}>
      <header className="dashboard-header">
        <div className="header-left">
          <button
            className="sidebar-toggle"
            onClick={handleToggleSidebar}
            title={isMobile 'Toggle Sidebar' : (sidebarCollapsed 'Expand Sidebar' : 'Collapse Sidebar')}
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <line x1="3" y1="6" x2="21" y2="6"></line>
              <line x1="3" y1="12" x2="21" y2="12"></line>
              <line x1="3" y1="18" x2="21" y2="18"></line>
            </svg>
          </button>
          <h1 className="app-title">Ketaharu</h1>
        </div>

        <div className="header-right">
          <div className="user-info">
            <span className="user-name">{user?.displayName || 'User'}</span>
          </div>

          <button
            className="theme-toggle"
            onClick={handleToggleTheme}
            title={isDarkTheme 'Switch to Light Mode' : 'Switch to Dark Mode'}
          >
            {isDarkTheme (
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <circle cx="12" cy="12" r="5" />
                <path d="M12 1v2M12 21v2M4.22 4.22l1.42 1.42M18.36 18.36l1.42 1.42M1 12h2M21 12h2M4.22 19.78l1.42-1.42M18.36 5.64l1.42-1.42" />
              </svg>
            ) : (
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z" />
              </svg>
            )}
          </button>

          <button
            className="profile-button"
            onClick={() => {
              console.log('Profile button clicked, setting showProfileModal to true');
              setShowProfileModal(true);
            }}
            title="Profile"
          >
            <img
              src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${user?.displayName || 'User'}`}
              alt={user?.displayName || 'User'}
            />
          </button>

          <button
            className="logout-button"
            onClick={handleLogout}
            title="Logout"
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" />
              <polyline points="16,17 21,12 16,7" />
              <line x1="21" y1="12" x2="9" y2="12" />
            </svg>
          </button>
        </div>
      </header>

      <div className="dashboard-content">
        {/* Mobile Sidebar Overlay */}
        {isMobileSidebarOpen && (
          <div className="mobile-sidebar-overlay" onClick={handleCloseMobileSidebar}></div>
        )}

        <Sidebar 
          isDarkTheme={isDarkTheme} 
          collapsed={sidebarCollapsed}
          onToggle={handleToggleSidebar}
          isMobileOpen={isMobileSidebarOpen}
          onMobileClose={handleCloseMobileSidebar}
        />
        <ChatWindow 
          contact={selectedContact}
          isDarkTheme={isDarkTheme}
        />
      </div>

      <ProfileModal
        isOpen={showProfileModal}
        onClose={() => {
          console.log('ProfileModal onClose called, setting showProfileModal to false');
          setShowProfileModal(false);
        }}
        user={user}
        isDarkTheme={isDarkTheme}
      />
    </div>
  );
};

export default Dashboard;

import React, { useState, useEffect } from 'react';
import { useChat } from '../../chat/context/ChatContext';
import '../css/GroupMembersModal.css';
import { useAuth } from '../../auth/context/AuthContext';

const GroupMembersModal = ({ isOpen, onClose, group }) => {
  const { friends, addMemberToGroup, removeMemberFromGroup, getGroupMembers } = useChat();
  const { user } = useAuth();

  const [groupMembers, setGroupMembers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Check if current user can remove members (admin and not group creator)
  const canRemoveMembers = group?.role === 'admin' && group?.createdBy && group.createdBy !== user?.uid;

  useEffect(() => {
    if (isOpen && group) {
      loadGroupMembers();
    }
  }, [isOpen, group]);

  const loadGroupMembers = async () => {
    if (!group) return;

    setIsLoading(true);
    try {
      const members = await getGroupMembers(group.groupId);
      setGroupMembers(members);
    } catch (error) {
      console.error('Error loading group members:', error);
      setError('Failed to load group members');
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddMember = async (friendId) => {
    if (!group) return;

    setIsLoading(true);
    setError('');
    setSuccess('');

    try {
      await addMemberToGroup(group.groupId, friendId);
      setSuccess('Member added successfully!');
      await loadGroupMembers(); // Reload members
    } catch (error) {
      setError(error.message || 'Failed to add member');
    } finally {
      setIsLoading(false);
    }
  };

  const handleRemoveMember = async (memberId) => {
    if (!group) return;

    if (!window.confirm('Are you sure you want to remove this member from the group?')) {
      return;
    }

    setIsLoading(true);
    setError('');
    setSuccess('');

    try {
      await removeMemberFromGroup(group.groupId, memberId);
      setSuccess('Member removed successfully!');
      await loadGroupMembers(); // Reload members
    } catch (error) {
      setError(error.message || 'Failed to remove member');
    } finally {
      setIsLoading(false);
    }
  };

  const filteredFriends = friends.filter(friend => {
    const isAlreadyMember = groupMembers.some(member => member.uid === friend.uid);
    const matchesSearch = friend.displayName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         friend.email?.toLowerCase().includes(searchTerm.toLowerCase());
    return !isAlreadyMember && matchesSearch;
  });

  if (!isOpen) return null;

  return (
    <div className="group-members-modal-overlay" onClick={onClose}>
      <div className="group-members-modal" onClick={e => e.stopPropagation()}>
        <div className="group-members-modal-header">
          <h2>Manage Group Members</h2>
          <button className="close-button" onClick={onClose}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M18 6L6 18M6 6l12 12" />
            </svg>
          </button>
        </div>

        <div className="group-members-modal-content">
          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {success && (
            <div className="success-message">
              {success}
            </div>
          )}

          <div className="group-info">
            <h3>{group?.name}</h3>
            <p>Current Members: {groupMembers.length}</p>
          </div>

          {/* Current Members Section */}
          <div className="current-members-section">
            <h4>Current Members</h4>
            {isLoading (
              <div className="loading">Loading members...</div>
            ) : groupMembers.length > 0 (
              <div className="members-list">
                {groupMembers.map(member => (
                  <div key={member.uid} className="member-item">
                    <div className="member-info">
                      <img
                        src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${member.displayName}`}
                        alt={member.displayName}
                        className="member-avatar"
                      />
                      <div className="member-details">
                        <div className="member-name">{member.displayName}</div>
                        <div className="member-email">{member.email}</div>
                        <div className="member-role">{member.role}</div>
                      </div>
                    </div>
                    {canRemoveMembers && (
                      <button
                        className="remove-member-btn"
                        onClick={() => handleRemoveMember(member.uid)}
                        disabled={isLoading}
                        title="Remove member"
                      >
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <path d="M18 6L6 18M6 6l12 12" />
                        </svg>
                      </button>
                    )}
                  </div>
                ))}
              </div>
            ) : (
              <div className="no-members">No members found</div>
            )}
          </div>

          {/* Add Members Section */}
          <div className="add-members-section">
            <h4>Add New Members</h4>
            <div className="member-search">
              <input
                type="text"
                placeholder="Search friends to add..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="member-search-input"
              />
            </div>

            {filteredFriends.length > 0 (
              <div className="available-friends-list">
                {filteredFriends.map(friend => (
                  <div key={friend.uid} className="friend-item">
                    <div className="friend-info">
                      <img
                        src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${friend.displayName}`}
                        alt={friend.displayName}
                        className="friend-avatar"
                      />
                      <div className="friend-details">
                        <div className="friend-name">{friend.displayName}</div>
                        <div className="friend-email">{friend.email}</div>
                      </div>
                    </div>
                    <button
                      className="add-member-btn"
                      onClick={() => handleAddMember(friend.uid)}
                      disabled={isLoading}
                    >
                      Add
                    </button>
                  </div>
                ))}
              </div>
            ) : searchTerm (
              <div className="no-results">No friends found matching your search</div>
            ) : (
              <div className="no-friends">No friends available to add</div>
            )}
          </div>
        </div>

        <div className="group-members-modal-actions">
          <button
            className="btn btn-primary"
            onClick={onClose}
            disabled={isLoading}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default GroupMembersModal;

import React, { useState } from 'react';
import { useChat } from '../../chat/context/ChatContext';
import '../css/GroupModal.css';

const GroupModal = ({ isOpen, onClose, mode = 'create', group = null }) => {
  const { friends, createGroup } = useChat();

  const [groupName, setGroupName] = useState(group?.name || '');
  const [selectedMembers, setSelectedMembers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!groupName.trim() || selectedMembers.length === 0) return;

    setIsSubmitting(true);
    try {
      if (mode === 'create') {
        await createGroup(groupName, selectedMembers);
        onClose();
      }
    } catch (error) {
      console.error('Error creating group:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleMemberToggle = (memberId) => {
    setSelectedMembers(prev => 
      prev.includes(memberId) prev.filter(id => id !== memberId)
        : [...prev, memberId]
    );
  };

  const filteredFriends = friends.filter(friend =>
    friend.displayName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    friend.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (!isOpen) return null;

  return (
    <div className="group-modal-overlay" onClick={onClose}>
      <div className="group-modal" onClick={e => e.stopPropagation()}>
        <div className="group-modal-header">
          <h2>{mode === 'create' 'Create New Group' : 'Edit Group'}</h2>
          <button className="close-button" onClick={onClose}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M18 6L6 18M6 6l12 12" />
            </svg>
          </button>
        </div>

        <form onSubmit={handleSubmit} className="group-modal-form">
          <div className="form-group">
            <label htmlFor="groupName">Group Name</label>
            <input
              id="groupName"
              type="text"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
              placeholder="Enter group name"
              required
              maxLength={50}
            />
          </div>

          <div className="form-group">
            <label>Select Members ({selectedMembers.length} selected)</label>
            <div className="member-search">
              <input
                type="text"
                placeholder="Search friends..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="member-search-input"
              />
            </div>

            <div className="member-list">
              {filteredFriends.map(friend => (
                <div
                  key={friend.uid}
                  className={`member-item ${selectedMembers.includes(friend.uid) 'selected' : ''}`}
                  onClick={() => handleMemberToggle(friend.uid)}
                >
                  <div className="member-avatar">
                    <img
                      src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${friend.displayName}`}
                      alt={friend.displayName}
                    />
                  </div>
                  <div className="member-info">
                    <div className="member-name">{friend.displayName}</div>
                    <div className="member-email">{friend.email}</div>
                  </div>
                  <div className="member-checkbox">
                    {selectedMembers.includes(friend.uid) && (
                      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <path d="M20 6L9 17l-5-5" />
                      </svg>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="form-actions">
            <button
              type="button"
              className="btn btn-outline"
              onClick={onClose}
              disabled={isSubmitting}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={!groupName.trim() || selectedMembers.length === 0 || isSubmitting}
            >
              {isSubmitting 'Creating...' : mode === 'create' 'Create Group' : 'Update Group'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default GroupModal;

