              <path d="M12 1v2M12 21v2M4.22 4.22l1.42 1.42M18.36 18.36l1.42 1.42M1 12h2M21 12h2M4.22 19.78l1.42-1.42M18.36 5.64l1.42-1.42" />
            </svg>
          ) : (
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z" />
            </svg>
          )}
        </button>

        <button
          className="profile-button"
          onClick={onProfileClick}
          title="Profile"
        >
          <img
            src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${user?.displayName || 'User'}`}
            alt={user?.displayName || 'User'}
          />
        </button>

        <button
          className="logout-button"
          onClick={onLogout}
          title="Logout"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" />
            <polyline points="16,17 21,12 16,7" />
            <line x1="21" y1="12" x2="9" y2="12" />
          </svg>
        </button>
      </div>
    </header>
  );
};

export default Header;

import React from 'react';
import '../css/Message.css';

const Message = ({ message, isOwnMessage, senderName, isGroup, isDarkTheme }) => {
  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit',
      hour12: true 
    });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return 'Today';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return 'Yesterday';
    } else {
      return date.toLocaleDateString();
    }
  };

  return (
    <div className={`message ${isOwnMessage 'own' : 'other'}`}>
      {isGroup && !isOwnMessage && (
        <div className="message-sender">
          <span className="sender-name">{senderName}</span>
        </div>
      )}

      <div className="message-content">
        <div className="message-text">{message.content}</div>
        <div className="message-meta">
          <span className="message-time">{formatTime(message.timestamp)}</span>
          {isOwnMessage && (
            <span className="message-status">
              {message.read '‚úì‚úì' : '‚úì'}
            </span>
          )}
        </div>
      </div>

      <div className="message-date">
        {formatDate(message.timestamp)}
      </div>
    </div>
  );
};

export default Message;

import React, { useState, useEffect, useRef } from 'react';
import { useChat } from '../../chat/context/ChatContext';
import '../css/MessageInput.css';

const MessageInput = ({ contact, isDarkTheme }) => {
  const [message, setMessage] = useState('');
  const { sendMessage, startTyping, stopTyping } = useChat();

  const typingTimeoutRef = useRef(null);
  const isTypingRef = useRef(false);

  useEffect(() => {
    // Clear any existing timeout
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    if (message.trim() && !isTypingRef.current) {
      isTypingRef.current = true;
      if (contact.type === 'group') {
        startTyping(contact.groupId, true);
      } else {
        startTyping(contact.uid, false);
      }
    }

    if (message.trim()) {
      typingTimeoutRef.current = setTimeout(() => {
        isTypingRef.current = false;
        if (contact.type === 'group') {
          stopTyping(contact.groupId, true);
        } else {
          stopTyping(contact.uid, false);
        }
      }, 2000);
    } else {
      isTypingRef.current = false;
      if (contact.type === 'group') {
        stopTyping(contact.groupId, true);
      } else {
        stopTyping(contact.uid, false);
      }
    }

    return () => {
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
    };
  }, [message, contact, startTyping, stopTyping]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('=== handleSubmit called ===');
    console.log('Message:', message);
    console.log('Contact:', contact);

    if (!message.trim() || !contact) {
      console.log('Early return - no message or contact');
      return;
    }

    try {
      console.log('About to send message...');

      // Stop typing immediately when sending message
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
        typingTimeoutRef.current = null;
      }

      isTypingRef.current = false;
      if (contact.type === 'group') {
        stopTyping(contact.groupId, true);
      } else {
        stopTyping(contact.uid, false);
      }

      if (contact.type === 'group') {
        console.log('Sending group message to:', contact.groupId);
        await sendMessage(message, contact.groupId, true);
      } else {
        console.log('Sending direct message to:', contact.uid);
        await sendMessage(message, contact.uid, false);
      }

      console.log('Message sent successfully, clearing input');
      setMessage('');
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  if (!contact) return null;

  return (
    <div className="message-input-container">
      <form onSubmit={handleSubmit} className="message-form">
        <div className="input-wrapper">
          <textarea
            className="message-input"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder={
              contact.type === 'group' `Message ${contact.name}...`
                : `Message ${contact.displayName}...`
            }
            rows="1"
            maxLength="1000"
          />
          <button
            type="submit"
            className="send-button"
            disabled={!message.trim()}
            title="Send message"
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M22 2L11 13M22 2l-7 20-4-9-9-4 20-7z" />
            </svg>
          </button>
        </div>
      </form>
    </div>
  );
};

export default MessageInput;

import React from 'react';
import { useAuth } from '../../auth/context/AuthContext';
import Message from './Message';
import '../css/MessageList.css';

const MessageList = ({ messages, contact, isDarkTheme }) => {
  const { user } = useAuth();

  if (!messages || messages.length === 0) {
    return null;
  }

  return (
    <div className="message-list">
      {messages.map((message) => {
        const isOwnMessage = message.sender === user.uid;
        const senderName = isOwnMessage 'You' 
          : (contact.type === 'group' message.senderDisplayName : contact.displayName);

        return (
          <Message
            key={message.id}
            message={message}
            isOwnMessage={isOwnMessage}
            senderName={senderName}
            isGroup={contact.type === 'group'}
            isDarkTheme={isDarkTheme}
          />
        );
      })}
    </div>
  );
};

export default MessageList;

import React from 'react';
import '../css/SearchBar.css';

const SearchBar = ({ value, onChange, onSearch, placeholder, isDarkTheme }) => {
  const handleInputChange = (e) => {
    const newValue = e.target.value;
    onChange(newValue);

    // Trigger search as user types (with debouncing)
    if (onSearch) {
      // Clear any existing timeout
      if (window.searchTimeout) {
        clearTimeout(window.searchTimeout);
      }

      // Set new timeout for search
      window.searchTimeout = setTimeout(() => {
        onSearch(newValue);
      }, 500); // 500ms delay for better performance
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && onSearch) {
      onSearch(value);
    }
  };

  const handleClear = () => {
    onChange('');
    if (onSearch) {
      onSearch('');
    }
  };

  return (
    <div className={`search-bar ${isDarkTheme 'dark' : ''}`}>
      <div className="search-icon">
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <circle cx="11" cy="11" r="8"></circle>
          <path d="m21 21-4.35-4.35"></path>
        </svg>
      </div>
      <input
        type="text"
        className="search-input"
        value={value}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress}
        placeholder={placeholder}
      />
      {value && (
        <button 
          className="clear-search"
          onClick={handleClear}
          aria-label="Clear search"
        >
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <line x1="18" y1="6" x2="6" y2="18"></line>
            <line x1="6" y1="6" x2="18" y2="18"></line>
          </svg>
        </button>
      )}
    </div>
  );
};

export default SearchBar;

import React, { useState, useEffect } from 'react';
import { useChat } from '../../chat/context/ChatContext';
import SearchBar from './SearchBar';
import ContactItem from './ContactItem';
import GroupModal from './GroupModal';
import '../css/Sidebar.css';

const Sidebar = ({ isDarkTheme, collapsed, onToggle, isMobileOpen, onMobileClose }) => {
  const {
    friends, friendRequests, pendingRequests, groups, groupInvites,
    searchResults, selectedFriend, selectedGroup, isSearching,
    setSelectedFriend, setSelectedGroup, searchUsers, sendFriendRequest,
    acceptFriendRequest, rejectFriendRequest, removeFriend,
    acceptGroupInvite, rejectGroupInvite, leaveGroup
  } = useChat();

  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('friends');
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  const handleSearch = async (term) => {
    console.log('handleSearch called with term:', term);
    if (term.trim()) {
      console.log('Calling searchUsers with:', term);
      await searchUsers(term);
    } else {
      console.log('Search term is empty, clearing results');
    }
  };

  const handleAddFriend = async (targetUser) => {
    try {
      await sendFriendRequest(targetUser.uid);
      setSearchTerm('');
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };

  const handleSelectFriend = (friend) => {
    setSelectedFriend(friend);
    setSelectedGroup(null);
    // Close mobile sidebar when selecting a contact
    if (isMobile && onMobileClose) {
      onMobileClose();
    }
  };

  const handleSelectGroup = (group) => {
    setSelectedGroup(group);
    setSelectedFriend(null);
    // Close mobile sidebar when selecting a contact
    if (isMobile && onMobileClose) {
      onMobileClose();
    }
  };

  const handleLeaveGroup = async (groupId) => {
    if (window.confirm('Are you sure you want to leave this group?')) {
      try {
        await leaveGroup(groupId);
      } catch (error) {
        console.error('Error leaving group:', error);
      }
    }
  };

  const isSelected = (item, type) => {
    if (type === 'friend') {
      return selectedFriend?.uid === item.uid;
    }
    if (type === 'group') {
      return selectedGroup?.groupId === item.groupId;
    }
    return false;
  };

  if (collapsed) {
    return (
      <aside className={`sidebar collapsed ${isDarkTheme 'dark' : ''}`}>
        <div className="sidebar-header">
          <button className="expand-button" onClick={onToggle}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M9 18l6-6-6-6" />
            </svg>
          </button>
        </div>
        <div className="sidebar-content">
          <div className="collapsed-stats">
            <div className="stat-item">
              <span className="stat-number">{friends.length}</span>
              <span className="stat-label">Friends</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{groups.length}</span>
              <span className="stat-label">Groups</span>
            </div>
          </div>
        </div>
      </aside>
    );
  }

  return (
    <aside className={`sidebar ${isDarkTheme 'dark' : ''} ${isMobileOpen 'open' : ''}`}>
      <div className="sidebar-header">
        {/* Mobile Close Button */}
        {isMobile && (
          <button className="mobile-close-btn" onClick={onMobileClose}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M18 6L6 18M6 6l12 12" />
            </svg>
          </button>
        )}

        <h2>Chat</h2>
        <div className="sidebar-tabs">
          <button
            className={`tab-button ${activeTab === 'friends' 'active' : ''}`}
            onClick={() => setActiveTab('friends')}
          >
            Friends
          </button>
          <button
            className={`tab-button ${activeTab === 'groups' 'active' : ''}`}
            onClick={() => setActiveTab('groups')}
          >
            Groups
          </button>
        </div>
        {activeTab === 'groups' && (
          <button
            className="create-group-btn"
            onClick={() => setShowGroupModal(true)}
            title="Create New Group"
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M12 5v14M5 12h14" />
            </svg>
          </button>
        )}
      </div>

      <div className="sidebar-content">
        {activeTab === 'friends' (
          <>
            {/* Search Section */}
            <div className="search-section">
              <SearchBar
                value={searchTerm}
                onChange={setSearchTerm}
                onSearch={handleSearch}
                placeholder="Search users..."
                isDarkTheme={isDarkTheme}
              />

              {/* Show loading state */}
              {isSearching && (
                <div className="search-loading">
                  <p>Searching...</p>
                </div>
              )}

              {/* Show search results */}
              {searchResults.length > 0 && !isSearching && (
                <div className="search-results">
                  <div className="search-results-header">
                    <span>Found {searchResults.length} user(s)</span>
                  </div>
                  {searchResults.map(user => (
                    <div key={user.uid} className="search-result-item">
                      <div className="search-result-info">
                        <img
                          src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${user.displayName}`}
                          alt={user.displayName}
                        />
                        <div>
                          <div className="search-result-name">{user.displayName}</div>
                          <div className="search-result-email">{user.email}</div>
                        </div>
                      </div>
                      <button
                        className="add-friend-btn"
                        onClick={() => handleAddFriend(user)}
                      >
                        Add Friend
                      </button>
                    </div>
                  ))}
                </div>
              )}

              {/* Show no results message */}
              {searchTerm && searchResults.length === 0 && !isSearching && (
                <div className="search-no-results">
                  <p>No users found matching "{searchTerm}"</p>
                </div>
              )}
            </div>

            {/* Friend Requests Section */}
            {friendRequests.length > 0 && (
              <div className="friend-requests-section">
                <h3>Friend Requests ({friendRequests.length})</h3>
                {friendRequests.map(request => (
                  <div key={request.uid} className="friend-request-item">
                    <div className="request-info">
                      <img
                        src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${request.displayName}`}
                        alt={request.displayName}
                      />
                      <div>
                        <div className="request-name">{request.displayName}</div>
                        <div className="request-email">{request.email}</div>
                      </div>
                    </div>
                    <div className="request-actions">
                      <button
                        className="accept-btn"
                        onClick={() => acceptFriendRequest(request.uid)}
                      >
                        Accept
                      </button>
                      <button
                        className="decline-btn"
                        onClick={() => rejectFriendRequest(request.uid)}
                      >
                        Decline
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {/* Pending Requests Section */}
            {pendingRequests.length > 0 && (
              <div className="pending-requests-section">
                <h3>Pending Requests ({pendingRequests.length})</h3>
                {pendingRequests.map(request => (
                  <div key={request.uid} className="pending-request-item">
                    <div className="request-info">
                      <img
                        src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${request.displayName}`}
                        alt={request.displayName}
                      />
                      <div>
                        <div className="request-name">{request.displayName}</div>
                        <div className="request-email">{request.email}</div>
                      </div>
                    </div>
                    <div className="request-status">
                      <span className="status pending">Pending</span>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {/* Friends List */}
            <div className="friends-section">
              <h3>Friends ({friends.length})</h3>
              {friends.length > 0 (
                friends.map(friend => (
                  <ContactItem
                    key={friend.uid}
                    contact={friend}
                    isSelected={isSelected(friend, 'friend')}
                    onClick={() => handleSelectFriend(friend)}
                    onRemove={() => removeFriend(friend.uid)}
                    isDarkTheme={isDarkTheme}
                  />
                ))
              ) : (
                <div className="no-friends">
                  <p>No friends yet

(
                friends.map(friend => (
                  <ContactItem
                    key={friend.uid}
                    contact={friend}
                    isSelected={isSelected(friend, 'friend')}
                    onClick={() => handleSelectFriend(friend)}
                    onRemove={() => removeFriend(friend.uid)}
                    isDarkTheme={isDarkTheme}
                  />
                ))
              ) : (
                <div className="no-friends">
                  <p>No friends yet Search for users to add them!</p>
                </div>
              )}
            </div>
          </>
        ) : (
          <>
            {/* Group Invites Section */}
            {groupInvites.length > 0 && (
              <div className="group-invites-section">
                <h3>Group Invites ({groupInvites.length})</h3>
                {groupInvites.map(invite => (
                  <div key={invite.groupId} className="group-invite-item">
                    <div className="group-info">
                      <div className="group-avatar">
                        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" />
                          <circle cx="9" cy="7" r="4" />
                          <path d="M23 21v-2a4 4 0 0 0-3-3.87" />
                          <path d="M16 3.13a4 4 0 0 1 0 7.75" />
                        </svg>
                      </div>
                      <div>
                        <div className="group-name">{invite.groupName}</div>
                        <div className="invite-from">Invited by {invite.invitedBy}</div>
                        <div className="invite-time">{new Date(invite.timestamp).toLocaleDateString()}</div>
                      </div>
                    </div>
                    <div className="invite-actions">
                      <button
                        className="accept-btn"
                        onClick={() => acceptGroupInvite(invite.groupId)}
                      >
                        Accept
                      </button>
                      <button
                        className="decline-btn"
                        onClick={() => rejectGroupInvite(invite.groupId)}
                      >
                        Decline
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {/* Groups List */}
            <div className="groups-section">
              <h3>Groups ({groups.length})</h3>
              {groups.length > 0 (
                groups.map(group => (
                  <div
                    key={group.groupId}
                    className={`group-item ${isSelected(group, 'group') 'selected' : ''}`}
                    onClick={() => handleSelectGroup(group)}
                  >
                    <div className="group-avatar">
                      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" />
                        <circle cx="9" cy="7" r="4" />
                        <path d="M23 21v-2a4 4 0 0 0-3-3.87" />
                        <path d="M16 3.13a4 4 0 0 1 0 7.75" />
                      </svg>
                    </div>
                    <div className="group-info">
                      <div className="group-name">{group.name || 'Unnamed Group'}</div>
                      <div className="group-role">{group.role}</div>
                    </div>
                    <div className="group-actions">
                      <button
                        className="leave-group-btn"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleLeaveGroup(group.groupId);
                        }}
                        title="Leave Group"
                      >
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <path d="M18 6L6 18M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-groups">
                  <p>No groups yet Create one to get started!</p>
                </div>
              )}
            </div>
          </>
        )}
      </div>

      {/* Group Modal */}
      <GroupModal
        isOpen={showGroupModal}
        onClose={() => setShowGroupModal(false)}
        mode="create"
      />
    </aside>
  );
};

export default Sidebar;

