}

.dark-theme .send-button:hover:not(:disabled) {
  box-shadow: 0 4px 12px rgba(139, 92, 246, 0.4);
}

.send-button:active {
  transform: translateY(0);
}

.send-button:disabled {
  background: #d1d5db;
  cursor: not-allowed;
  transform: none;
  box-shadow: none;
}

.dark-theme .send-button:disabled {
  background: #4b5563;
}

.send-button svg {
  width: 16px;
  height: 16px;
  transition: transform 0.2s ease;
}

.send-button:hover:not(:disabled) svg {
  transform: scale(1.1);
}

/* Responsive Design */
@media (max-width: 768px) {
  .message-input-container {
    padding: 1rem;
  }

  .input-wrapper {
    padding: 0.375rem;
  }

  .message-input {
    font-size: 0.8rem;
    padding: 0.375rem 0.625rem;
  }

  .send-button {
    width: 32px;
    height: 32px;
  }

  .send-button svg {
    width: 14px;
    height: 14px;
  }
}

@media (max-width: 480px) {
  .message-input-container {
    padding: 0.75rem;
  }

  .input-wrapper {
    padding: 0.25rem;
    border-radius: 1.25rem;
  }

  .message-input {
    font-size: 0.75rem;
    padding: 0.25rem 0.5rem;
  }

  .send-button {
    width: 28px;
    height: 28px;
  }

  .send-button svg {
    width: 12px;
    height: 12px;
  }
}

.message-list {
  display: flex;
  flex-direction: column;
  padding: 1rem;
  gap: 0.5rem;
  overflow-y: auto;
  flex: 1;
  transition: all 0.3s ease;
}

.message-list::-webkit-scrollbar {
  width: 6px;
}

.message-list::-webkit-scrollbar-track {
  background: transparent;
  transition: background 0.3s ease;
}

.message-list::-webkit-scrollbar-thumb {
  background: rgba(0, 0, 0, 0.2);
  border-radius: 3px;
  transition: background 0.3s ease;
}

.dark-theme .message-list::-webkit-scrollbar-thumb {
  background: rgba(255, 255, 255, 0.2);
}

.message-list::-webkit-scrollbar-thumb:hover {
  background: rgba(0, 0, 0, 0.3);
  transition: background 0.3s ease;
}

.dark-theme .message-list::-webkit-scrollbar-thumb:hover {
  background: rgba(255, 255, 255, 0.3);
}

/* Responsive Design */
@media (max-width: 768px) {
  .message-list {
    padding: 0.75rem;
  }
}

.message-list.empty {
  align-items: center;
  justify-content: center;
  height: 100%;
}

.empty-messages {
  text-align: center;
  color: #64748b;
}

.empty-messages p {
  margin: 0.25rem 0;
  font-size: 0.875rem;
}

.message-date {
  text-align: center;
  margin: 1rem 0;
  padding: 0.5rem;
}

.message-date::before {
  content: '';
  display: block;
  height: 1px;
  background-color: #e2e8f0;
  margin-bottom: 0.5rem;
}

.message-date {
  font-size: 0.75rem;
  color: #64748b;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.profile-modal-backdrop {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 1rem;
  animation: fadeIn 0.3s ease-out;
}

.profile-modal {
  background: white;
  border-radius: 1rem;
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 500px;
  max-height: 90vh;
  overflow-y: auto;
  animation: slideIn 0.3s ease-out;
}

.profile-modal.dark {
  background: #1e293b;
  color: white;
}

.modal-header {
  padding: 1.5rem 1.5rem 1rem;
  border-bottom: 1px solid #e2e8f0;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.profile-modal.dark .modal-header {
  border-bottom-color: #334155;
}

.modal-header h2 {
  font-size: 1.5rem;
  font-weight: 600;
  color: #1e293b;
  margin: 0;
}

.profile-modal.dark .modal-header h2 {
  color: white;
}

.close-button {
  background: none;
  border: none;
  color: #64748b;
  cursor: pointer;
  padding: 0.5rem;
  border-radius: 0.5rem;
  transition: all 0.2s ease;
}

.close-button:hover {
  background-color: #f1f5f9;
  color: #475569;
}

.profile-modal.dark .close-button {
  color: #9ca3af;
}

.profile-modal.dark .close-button:hover {
  background-color: #334155;
  color: white;
}

.modal-content {
  padding: 1.5rem;
}

.profile-section {
  text-align: center;
  margin-bottom: 2rem;
  padding-bottom: 1.5rem;
  border-bottom: 1px solid #e2e8f0;
}

.profile-modal.dark .profile-section {
  border-bottom-color: #334155;
}

.profile-avatar-large {
  margin-bottom: 1rem;
}

.profile-avatar-large img {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  object-fit: cover;
}

.profile-name {
  font-size: 1.25rem;
  font-weight: 600;
  color: #1e293b;
  margin: 0 0 0.5rem 0;
}

.profile-modal.dark .profile-name {
  color: white;
}

.profile-email {
  color: #64748b;
  font-size: 0.875rem;
  margin: 0;
}

.profile-modal.dark .profile-email {
  color: #9ca3af;
}

.settings-section,
.account-section {
  margin-bottom: 2rem;
}

.settings-section h4,
.account-section h4 {
  font-size: 1rem;
  font-weight: 600;
  color: #1e293b;
  margin: 0 0 1rem 0;
}

.profile-modal.dark .settings-section h4,
.profile-modal.dark .account-section h4 {
  color: white;
}

.setting-item {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.75rem 0;
  border-bottom: 1px solid #f1f5f9;
}

.setting-item:last-child {
  border-bottom: none;
}

.profile-modal.dark .setting-item {
  border-bottom-color: #334155;
}

.setting-item span {
  color: #374151;
  font-size: 0.875rem;
}

.profile-modal.dark .setting-item span {
  color: #e5e7eb;
}

/* Toggle switch */
.toggle {
  position: relative;
  display: inline-block;
  width: 44px;
  height: 24px;
}

.toggle input {
  opacity: 0;
  width: 0;
  height: 0;
}

.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #cbd5e1;
  transition: 0.3s;
  border-radius: 24px;
}

.slider:before {
  position: absolute;
  content: "";
  height: 18px;
  width: 18px;
  left: 3px;
  bottom: 3px;
  background-color: white;
  transition: 0.3s;
  border-radius: 50%;
}

input:checked + .slider {
  background-color: #6366f1;
}

.profile-modal.dark input:checked + .slider {
  background-color: #8b5cf6;
}

input:checked + .slider:before {
  transform: translateX(20px);
}

/* Button styles */
.btn {
  display: inline-block;
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 0.5rem;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  text-align: center;
  text-decoration: none;
}

.btn-outline {
  background: transparent;
  color: #6366f1;
  border: 2px solid #6366f1;
}

.btn-outline:hover {
  background: #6366f1;
  color: white;
}

.profile-modal.dark .btn-outline {
  color: #8b5cf6;
  border-color: #8b5cf6;
}

.profile-modal.dark .btn-outline:hover {
  background: #8b5cf6;
  color: white;
}

.w-full {
  width: 100%;
}

.mb-4 {
  margin-bottom: 1rem;
}

/* Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(-20px) scale(0.95);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

/* Responsive design */
@media (max-width: 480px) {
  .profile-modal {
    margin: 0.5rem;
  }

  .modal-header,
  .modal-content {
    padding: 1rem;
  }

  .profile-avatar-large img {
    width: 60px;
    height: 60px;
  }
}

.search-bar {
