import React from 'react';
import { BrowserRouter } from 'react-router-dom';
import { AuthProvider } from '../core/context/AuthContext';
import AppRoutes from './routes';

function App() {
  return (
    <BrowserRouter>
      <AuthProvider>
        <div className="App">
          <AppRoutes />
        </div>
      </AuthProvider>
    </BrowserRouter>
  );
}

export default App;

import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from '../core/context/AuthContext';

// Layouts
import MainLayout from '../core/layout/MainLayout';
import DashboardLayout from '../core/layout/DashboardLayout';

// Pages
import LandingPage from '../features/landing/pages/LandingPage';
import LoginPage from '../features/auth/pages/LoginPage';
import SignupPage from '../features/auth/pages/SignupPage';
import ForgotPasswordPage from '../features/auth/pages/ForgotPasswordPage';
import Checkout from '../features/payment_gateway/component/CheckoutPage';
import CodeToDocPage from '../features/code_to_doc/page/codetodoc';

// Protected Route Component
const ProtectedRoute = ({ children }) => {
    const { isAuthenticated } = useAuth();
    return isAuthenticated children : <Navigate to="/login" replace />;
};

// Public Route Component
const PublicRoute = ({ children }) => {
    return children;
};

// Public Route that doesn't redirect even if authenticated
const OpenRoute = ({ children }) => {
    return children;
};

const DashboardHome = () => null;

const AppRoutes = () => {
    const { user, logout } = useAuth();

    return (
        <Routes>
            {/* Public Routes */}
            <Route
                path="/"
                element={
                    <PublicRoute>
                        <MainLayout user={user} onLogout={logout}>
                            <LandingPage />
                        </MainLayout>
                    </PublicRoute>
                }
            />

            <Route
                path="/login"
                element={
                    <PublicRoute>
                        <LoginPage />
                    </PublicRoute>
                }
            />

            <Route
                path="/signup"
                element={
                    <PublicRoute>
                        <SignupPage />
                    </PublicRoute>
                }
            />

            <Route
                path="/forgot-password"
                element={
                    <PublicRoute>
                        <ForgotPasswordPage />
                    </PublicRoute>
                }
            />

            {/* Open Routes - Accessible by anyone */}
            <Route
                path="/checkout"
                element={
                    <OpenRoute>
                        <Checkout />
                    </OpenRoute>
                }
            />

            {/* Protected Routes */}
            <Route
                path="/dashboard"
                element={
                    <ProtectedRoute>
                        <DashboardLayout>
                            <DashboardHome />
                        </DashboardLayout>
                    </ProtectedRoute>
                }
            />
            <Route
                path="/dashboard/code-to-doc"
                element={
                    <ProtectedRoute>
                        <DashboardLayout>
                            <CodeToDocPage />
                        </DashboardLayout>
                    </ProtectedRoute>
                }
            />

            {/* Catch all route */}
            <Route
                path="*"
                element={<Navigate to="/" replace />}
            />
        </Routes>
    );
};

export default AppRoutes;

.App {
  text-align: center;
}

.App-logo {
  height: 40vmin;
  pointer-events: none;
}

@media (prefers-reduced-motion: no-preference) {
  .App-logo {
    animation: App-logo-spin infinite 20s linear;
  }
}

.App-header {
  background-color: #282c34;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-size: calc(10px + 2vmin);
  color: white;
}

.App-link {
  color: #61dafb;
}

/* Dashboard shell */
.dashboard-shell {
  min-height: 100vh;
  background: #f7f8fb;
}

.dashboard-shell-content {
  margin-left: 260px;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  padding: 1.25rem 1.75rem;
}

.dashboard-shell.collapsed .dashboard-shell-content {
  margin-left: 70px;
  transition: margin-left 0.3s ease;
}

.dashboard-shell.expanded .dashboard-shell-content {
  margin-left: 260px;
  transition: margin-left 0.3s ease;
}

.dashboard-shell-main {
  flex: 1;
}

@media (max-width: 1024px) {
  .dashboard-shell {
    padding-top: 5rem;
  }

  .dashboard-shell-content {
    margin-left: 0;
    padding: 1rem;
  }
}

@keyframes App-logo-spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

