import React from 'react';
import '../css/Button.css';

const Button = ({ 
  children, 
  variant = 'primary', 
  size = 'md', 
  disabled = false, 
  loading = false,
  className = '',
  onClick,
  type = 'button',
  ...props 
}) => {
  const baseClasses = 'btn';
  
  const variants = {
    primary: 'btn-primary',
    secondary: 'btn-secondary',
    outline: 'btn-outline',
    ghost: 'btn-ghost',
    danger: 'btn-danger'
  };
  
  const sizes = {
    sm: 'btn-sm',
    md: '',
    lg: 'btn-lg',
    xl: 'btn-xl'
  };
  
  const classes = `${baseClasses} ${variants[variant]} ${sizes[size]} ${className}`;
  
  return (
    <button
      type={type}
      className={classes}
      disabled={disabled || loading}
      onClick={onClick}
      {...props}
    >
      {loading && (
        <svg className="loading-spinner" fill="none" viewBox="0 0 24 24">
          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
        </svg>
      )}
      {children}
    </button>
  );
};

export default Button;

import React from 'react';
import '../css/Card.css';

const Card = ({ 
  children, 
  className = '', 
  hover = false, 
  padding = 'default',
  ...props 
}) => {
  const baseClasses = 'card';
  const hoverClasses = hover 'card-hover' : '';
  
  const paddingClasses = {
    none: 'card-padding-none',
    sm: 'card-padding-sm',
    default: 'card-padding-default',
    lg: 'card-padding-lg'
  };
  
  const classes = `${baseClasses} ${hoverClasses} ${paddingClasses[padding]} ${className}`;
  
  return (
    <div className={classes} {...props}>
      {children}
    </div>
  );
};

const CardHeader = ({ children, className = '', ...props }) => (
  <div className={`card-header ${className}`} {...props}>
    {children}
  </div>
);

const CardTitle = ({ children, className = '', ...props }) => (
  <h3 className={`card-title ${className}`} {...props}>
    {children}
  </h3>
);

const CardDescription = ({ children, className = '', ...props }) => (
  <p className={`card-description ${className}`} {...props}>
    {children}
  </p>
);

const CardContent = ({ children, className = '', ...props }) => (
  <div className={`card-content ${className}`} {...props}>
    {children}
  </div>
);

const CardFooter = ({ children, className = '', ...props }) => (
  <div className={`card-footer ${className}`} {...props}>
    {children}
  </div>
);

Card.Header = CardHeader;
Card.Title = CardTitle;
Card.Description = CardDescription;
Card.Content = CardContent;
Card.Footer = CardFooter;

export default Card;

import React from 'react';
import '../css/InputField.css';

const InputField = ({
  label,
  type = 'text',
  name,
  value,
  onChange,
  placeholder,
  error,
  required = false,
  disabled = false,
  className = '',
  ...props
}) => {
  const inputClasses = `input-field-input ${error 'input-field-error' : ''} ${disabled 'input-field-disabled' : ''} ${className}`;
  
  return (
    <div className={`input-field ${error 'input-field-error' : ''} ${disabled 'input-field-disabled' : ''}`}>
      {label && (
        <label htmlFor={name} className="input-field-label">
          {label}
          {required && <span className="input-field-required">*</span>}
        </label>
      )}
      
      {type === 'file' (
        <div className="input-field-with-icon">
          <input
            type="file"
            id={name}
            name={name}
            onChange={onChange}
            disabled={disabled}
            className="input-field-file"
            {...props}
          />
          <div className="input-field-file-text">
            <div className="input-field-file-icon">
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
              </svg>
            </div>
            <p>Click to upload or drag and drop</p>
            <p>PDF, DOC, TXT files up to 10MB</p>
          </div>
        </div>
      ) : (
        <input
          type={type}
          id={name}
          name={name}
          value={value}
          onChange={onChange}
          placeholder={placeholder}
          disabled={disabled}
          className={inputClasses}
          {...props}
        />
      )}
      
      {error && (
        <p className="input-field-error-message">{error}</p>
      )}
    </div>
  );
};

export default InputField;

import React, { useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import dashboardIcon from '../../assets/sidebar_icons/dashboard_icon.png';
import botIcon from '../../assets/sidebar_icons/bot_logo.png';
import docIcon from '../../assets/sidebar_icons/doc_analysis_logo.png';
import reportIcon from '../../assets/sidebar_icons/report_icon.png';
import faqIcon from '../../assets/sidebar_icons/faq_logo.png';
import settingsIcon from '../../assets/sidebar_icons/settings_icon.png';
import '../css/Sidebar.css';

const Sidebar = ({ onSectionChange, onReportClick, collapsed: collapsedProp, onToggleCollapse }) => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [internalCollapsed, setInternalCollapsed] = useState(false);
  const [showUserInfo, setShowUserInfo] = useState(false);

  const collapsed = collapsedProp ? internalCollapsed;
  const toggleCollapse = onToggleCollapse ? (() => setInternalCollapsed((prev) => !prev));

  const menuItems = useMemo(
    () => [
      { label: 'Dashboard', icon: dashboardIcon, to: '/dashboard' },
      { label: 'Bot Setup', icon: botIcon },
      { label: 'Code To Document', icon: docIcon, to: '/dashboard/code-to-doc' },
      { divider: true },
      { label: 'Report', icon: reportIcon, onClick: onReportClick },
      { label: 'Help and Faq', icon: faqIcon },
      { label: 'Settings', icon: settingsIcon }
    ],
    [onReportClick]
  );

  const toggleUserInfo = () => setShowUserInfo((prev) => !prev);

  return (
    <div className={`sidebar ${collapsed 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <div
          className="logo-icon"
          role="button"
          tabIndex={0}
          onClick={() => navigate('/')}
          onKeyDown={(e) => (e.key === 'Enter' || e.key === ' ' navigate('/') : null)}
        >
          R
        </div>
        {!collapsed && (
          <button
            type="button"
            className="logo-text"
            onClick={() => navigate('/')}
          >
            Recall AI
          </button>
        )}
      </div>

      <div className="sidebar-menu">
        {menuItems.map((item, idx) =>
          item.divider (
            <hr key={`divider-${idx}`} className="menu-divider" />
          ) : (
            <div
              key={item.label}
              className="menu-item"
              onClick={() => {
                if (item.onClick) {
                  item.onClick();
                  return;
                }
                if (item.to) {
                  navigate(item.to);
                  return;
                }
                onSectionChange?.(item.label);
              }}
            >
              <img src={item.icon} alt={item.label} className="menu-icon" />
              {!collapsed && <span>{item.label}</span>}
            </div>
          )
        )}
      </div>

      <div className="sidebar-footer">
        <div className="user-toggle" onClick={toggleUserInfo}>
          <div className="user-icon">{(user?.name || 'U').charAt(0)}</div>
          {!collapsed && <span className="username">{user?.name || 'User'}</span>}
          {!collapsed && (
            <span className={`arrow-toggle ${showUserInfo 'open' : ''}`} aria-hidden="true">
              {showUserInfo '^' : 'v'}
            </span>
          )}
        </div>

        {!collapsed && showUserInfo && (
          <div className="user-details">
            <p><strong>Email:</strong> {user?.email || 'Not available'}</p>
            <button className="logout-btn" type="button" onClick={logout}>
              Logout
            </button>
          </div>
        )}

        <button className="collapse-btn" onClick={toggleCollapse}>
          {collapsed '>' : '<'}
        </button>
      </div>
    </div>
  );
};

export default Sidebar;

import React, { createContext, useContext, useReducer, useEffect } from 'react';
import useLocalStorage from '../hooks/useLocalStorage';
import { MOCK_DATA } from '../utils/constants';

const AuthContext = createContext();

const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return { ...state, loading: true, error: null };
    case 'LOGIN_SUCCESS':
      return { 
        ...state, 
        loading: false, 
        isAuthenticated: true, 
        user: action.payload,
        error: null 
      };
    case 'LOGIN_FAILURE':
      return { 
        ...state, 
        loading: false, 
        isAuthenticated: false, 
        user: null,
        error: action.payload 
      };
    case 'LOGOUT':
      return { 
        ...state, 
        isAuthenticated: false, 
        user: null, 
        error: null 
      };
    case 'CLEAR_ERROR':
      return { ...state, error: null };
    default:
      return state;
  }
};

const initialState = {
  isAuthenticated: false,
  user: null,
  loading: false,
  error: null
};

export const AuthProvider = ({ children }) => {
  const [storedAuth, setStoredAuth] = useLocalStorage('auth', initialState);
  const [state, dispatch] = useReducer(authReducer, storedAuth);

  // Update localStorage when state changes
  useEffect(() => {
    setStoredAuth(state);
  }, [state, setStoredAuth]);

  const login = async (email, password) => {
    dispatch({ type: 'LOGIN_START' });
    
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Mock validation
      if (email === 'demo@example.com' && password === 'password') {
        const user = { ...MOCK_DATA.USER, email };
        dispatch({ type: 'LOGIN_SUCCESS', payload: user });
        return { success: true };
      } else {
        throw new Error('Invalid email or password');
      }
    } catch (error) {
      dispatch({ type: 'LOGIN_FAILURE', payload: error.message });
      return { success: false, error: error.message };
    }
  };

  const signup = async (name, email, password) => {
    dispatch({ type: 'LOGIN_START' });
    
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Mock validation
      if (email && password && name) {
        const user = { 
          id: Date.now(),
          name, 
          email, 
          avatar: null,
          createdAt: new Date().toISOString()
        };
        dispatch({ type: 'LOGIN_SUCCESS', payload: user });
        return { success: true };
      } else {
        throw new Error('Please fill in all fields');
      }
    } catch (error) {
      dispatch({ type: 'LOGIN_FAILURE', payload: error.message });
      return { success: false, error: error.message };
    }
  };

  const logout = () => {
    dispatch({ type: 'LOGOUT' });
  };

  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  const value = {
    ...state,
    login,
    signup,
    logout,
    clearError
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

/* Button Component Styles */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  border-radius: var(--radius-xl);
  transition: all var(--transition-normal);
  cursor: pointer;
  border: none;
  text-decoration: none;
  font-family: inherit;
  padding: 0.75rem 1.5rem;
  font-size: var(--font-size-base);
  line-height: 1.5;
}

.btn:focus {
  outline: 2px solid transparent;
  outline-offset: 2px;
  box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.5);
}

.btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Button Variants */
.btn-primary {
  background: linear-gradient(135deg, #3B82F6, #2563EB);
  color: white;
  box-shadow: var(--shadow-lg);
  border: 1px solid rgba(59, 130, 246, 0.2);
}

.btn-primary:hover:not(:disabled) {
  background: linear-gradient(135deg, #2563EB, #1D4ED8);
  box-shadow: var(--shadow-xl);
  transform: translateY(-1px);
}

.btn-secondary {
  background: linear-gradient(135deg, #f3f4f6, #e5e7eb);
  color: var(--color-gray-900);
  box-shadow: var(--shadow-sm);
  border: 1px solid var(--color-gray-300);
}

.btn-secondary:hover:not(:disabled) {
  background: linear-gradient(135deg, #e5e7eb, #d1d5db);
  box-shadow: var(--shadow-md);
  transform: translateY(-1px);
}

.btn-outline {
  background: transparent;
  color: white;
  border: 2px solid rgba(255, 255, 255, 0.3);
  backdrop-filter: blur(4px);
}

.btn-outline:hover:not(:disabled) {
  background: rgba(255, 255, 255, 0.1);
  border-color: rgba(255, 255, 255, 0.5);
}

.btn-ghost {
  background: transparent;
  color: var(--color-gray-700);
  border: none;
}

.btn-ghost:hover:not(:disabled) {
  background: var(--color-gray-100);
  color: var(--color-gray-900);
}

.btn-danger {
  background: linear-gradient(135deg, #ef4444, #dc2626);
  color: white;
  box-shadow: var(--shadow-lg);
  border: 1px solid rgba(239, 68, 68, 0.2);
}

.btn-danger:hover:not(:disabled) {
  background: linear-gradient(135deg, #dc2626, #b91c1c);
  box-shadow: var(--shadow-xl);
  transform: translateY(-1px);
}

/* Button Sizes */
.btn-sm {
  padding: 0.5rem 1rem;
  font-size: var(--font-size-sm);
}

.btn-lg {
  padding: 1rem 3rem;
  font-size: var(--font-size-lg);
}

.btn-xl {
  padding: 1.25rem 4rem;
  font-size: var(--font-size-xl);
}

/* Loading spinner */
.btn .loading-spinner {
  animation: spin 1s linear infinite;
  margin-right: 0.5rem;
  width: 1rem;
  height: 1rem;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

/* Full width button */
.btn-full {
  width: 100%;
}

/* Button with icon */
.btn-icon {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
}

.btn-icon svg {
  width: 1.25rem;
  height: 1.25rem;
}

/* Card Component Styles */
.card {
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(4px);
  border-radius: var(--radius-2xl);
  box-shadow: var(--shadow-lg);
  border: 1px solid rgba(229, 231, 235, 0.5);
  transition: all var(--transition-slow);
}

.card:hover {
  box-shadow: var(--shadow-2xl);
  border-color: rgba(209, 213, 219, 0.5);
  background: rgba(255, 255, 255, 0.9);
  transform: translateY(-4px);
}

.card-dark {
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(40px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  color: white;
}

.card-dark:hover {
  background: rgba(255, 255, 255, 0.1);
  border-color: rgba(255, 255, 255, 0.2);
}

/* Card Padding Variants */
.card-padding-none {
  padding: 0;
}

.card-padding-sm {
  padding: var(--space-6);
}

.card-padding-default {
  padding: var(--space-8);
}

.card-padding-lg {
  padding: 2.5rem;
}

/* Card Header */
.card-header {
  margin-bottom: var(--space-6);
}

.card-title {
  font-size: var(--font-size-xl);
  font-weight: 700;
  color: var(--color-gray-900);
  margin: 0;
}

.card-description {
  font-size: var(--font-size-base);
  color: var(--color-gray-600);
  line-height: 1.625;
  margin: 0;
}

.card-content {
  /* Content area - no specific styles needed */
}

.card-footer {
  margin-top: var(--space-6);
  padding-top: var(--space-6);
  border-top: 1px solid rgba(229, 231, 235, 0.5);
}

/* Card with hover effects */
.card-hover {
  cursor: pointer;
}

.card-hover:hover {
  box-shadow: var(--shadow-2xl);
  transform: translateY(-8px);
}

/* Card variants */
.card-elevated {
  box-shadow: var(--shadow-xl);
}

.card-flat {
  box-shadow: none;
  border: 1px solid var(--color-gray-200);
}

.card-bordered {
  border: 2px solid var(--color-gray-200);
}

.card-bordered:hover {
  border-color: var(--color-gray-300);
}

/* Card with gradient background */
.card-gradient {
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.9), rgba(248, 250, 252, 0.9));
}

.card-gradient-dark {
  background: linear-gradient(135deg, rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.05));
}

/* Input Field Component Styles */
.input-field {
  margin-bottom: var(--space-4);
}

.input-field-label {
  display: block;
  font-size: var(--font-size-sm);
  font-weight: 600;
  color: var(--color-gray-700);
  margin-bottom: var(--space-2);
}

.input-field-required {
  color: #ef4444;
  margin-left: 0.25rem;
}

.input-field-input {
  display: block;
  width: 100%;
  padding: 0.75rem 1rem;
  border: 1px solid var(--color-gray-300);
  border-radius: var(--radius-xl);
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(4px);
  transition: all var(--transition-fast);
  font-family: inherit;
  font-size: var(--font-size-base);
  line-height: 1.5;
}

.input-field-input:focus {
  outline: none;
  border-color: var(--color-primary);
  box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.2);
}

.input-field-input::placeholder {
  color: var(--color-gray-400);
}

.input-field-input:hover:not(:focus) {
  border-color: var(--color-gray-400);
}

/* Input states */
.input-field-error .input-field-input {
  border-color: #fca5a5;
  background: rgba(254, 242, 242, 0.5);
}

.input-field-error .input-field-input:focus {
  border-color: #ef4444;
  box-shadow: 0 0 0 2px rgba(239, 68, 68, 0.2);
}

.input-field-disabled .input-field-input {
  background: rgba(249, 250, 251, 0.5);
  cursor: not-allowed;
  opacity: 0.6;
}

.input-field-error-message {
  margin-top: var(--space-2);
  font-size: var(--font-size-sm);
  color: #ef4444;
}

/* Input with icon */
.input-field-with-icon {
  position: relative;
}

.input-field-icon {
  position: absolute;
  left: 1rem;
  top: 50%;
  transform: translateY(-50%);
  width: 1.25rem;
  height: 1.25rem;
  color: var(--color-gray-400);
  pointer-events: none;
}

.input-field-with-icon .input-field-input {
  padding-left: 3rem;
}

/* Input with button */
.input-field-with-button {
  display: flex;
  gap: var(--space-2);
}

.input-field-with-button .input-field-input {
  flex: 1;
}

/* File input specific styles */
.input-field-file {
  position: relative;
  display: inline-block;
  cursor: pointer;
  padding: 0.75rem 1rem;
  border: 2px dashed var(--color-gray-300);
  border-radius: var(--radius-xl);
  background: rgba(249, 250, 251, 0.5);
  transition: all var(--transition-fast);
  text-align: center;
  width: 100%;
}

.input-field-file:hover {
  border-color: var(--color-primary);
  background: rgba(59, 130, 246, 0.05);
}

.input-field-file input[type="file"] {
  position: absolute;
  opacity: 0;
  width: 100%;
  height: 100%;
  cursor: pointer;
}

.input-field-file-text {
  color: var(--color-gray-600);
  font-size: var(--font-size-sm);
}

.input-field-file-icon {
  width: 2rem;
  height: 2rem;
  color: var(--color-gray-400);
  margin: 0 auto var(--space-2);
}

/* Textarea specific styles */
.input-field-textarea {
  min-height: 6rem;
  resize: vertical;
}

/* Select specific styles */
.input-field-select {
  background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='m6 8 4 4 4-4'/%3e%3c/svg%3e");
  background-position: right 0.5rem center;
  background-repeat: no-repeat;
  background-size: 1.5em 1.5em;
  padding-right: 2.5rem;
  appearance: none;
}

/* Checkbox and radio styles */
.input-field-checkbox,
.input-field-radio {
  display: flex;
  align-items: center;
  gap: var(--space-2);
}

.input-field-checkbox input,
.input-field-radio input {
  width: 1rem;
  height: 1rem;
  accent-color: var(--color-primary);
}

.input-field-checkbox label,
.input-field-radio label {
  font-size: var(--font-size-sm);
  color: var(--color-gray-700);
  cursor: pointer;
  margin: 0;
}

/* Input group styles */
.input-field-group {
  display: flex;
  gap: var(--space-2);
}

.input-field-group .input-field-input {
  border-radius: 0;
}

.input-field-group .input-field-input:first-child {
  border-top-left-radius: var(--radius-xl);
  border-bottom-left-radius: var(--radius-xl);
}

.input-field-group .input-field-input:last-child {
  border-top-right-radius: var(--radius-xl);
  border-bottom-right-radius: var(--radius-xl);
}

.input-field-group .input-field-input:not(:first-child):not(:last-child) {
  border-left: none;
  border-right: none;
}

.input-field-group .input-field-input:not(:first-child) {
  border-left: none;
}

.input-field-group .input-field-input:not(:last-child) {
  border-right: none;
}

