  padding: 3rem 1rem;
}

@media (min-width: 640px) {
  .auth-container {
    padding: 3rem 1.5rem;
  }
}

@media (min-width: 1024px) {
  .auth-container {
    padding: 3rem 2rem;
  }
}

.auth-form-wrapper {
  max-width: 28rem;
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

.auth-header {
  text-align: center;
}

.auth-logo {
  margin: 0 auto 1.5rem;
  width: 3rem;
  height: 3rem;
