  color: var(--color-primary);
  text-decoration: none;
  transition: color var(--transition-fast);
}

.auth-link:hover {
  color: var(--color-primary-dark);
}

.auth-form {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.auth-error {
  background: #fef2f2;
  border: 1px solid #fecaca;
