.auth-field-group {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.auth-remember {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.auth-remember input {
  width: 1rem;
  height: 1rem;
  accent-color: var(--color-primary);
}

.auth-remember label {
  font-size: var(--font-size-sm);
  color: var(--color-gray-900);
  cursor: pointer;
  margin: 0;
}

.auth-forgot-link {
