/* Auth form variants */
.auth-form-centered {
  text-align: center;
}

.auth-form-left {
  text-align: left;
}

/* Auth form with background image */
.auth-form-with-bg {
  background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="grid" width="10" height="10" patternUnits="userSpaceOnUse"><path d="M 10 0 L 0 0 0 10" fill="none" stroke="%23e5e7eb" stroke-width="0.5"/></pattern></defs><rect width="100" height="100" fill="url(%23grid)"/></svg>');
  background-size: 20px 20px;
  background-position: 0 0;
}

/* Auth form with gradient background */
.auth-form-gradient {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.auth-form-gradient .auth-form-wrapper {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: var(--radius-2xl);
  padding: 2rem;
  box-shadow: var(--shadow-xl);
}

/* Responsive adjustments */
@media (max-width: 640px) {
  .auth-container {
    padding: 2rem 1rem;
  }
  
  .auth-form-wrapper {
    max-width: 100%;
  }
  
  .auth-title {
    font-size: var(--font-size-2xl);
  }
  
  .auth-logo {
    width: 2.5rem;
    height: 2.5rem;
    font-size: var(--font-size-lg);
  }
}

/* Auth form animations */
.auth-form-enter {
  animation: authFormSlideIn 0.5s ease-out;
}

@keyframes authFormSlideIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Auth form loading state */
.auth-form-loading {
  opacity: 0.7;
  pointer-events: none;
}

.auth-form-loading .btn {
  position: relative;
  color: transparent;
}

.auth-form-loading .btn::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 1rem;
  height: 1rem;
  border: 2px solid transparent;
  border-top: 2px solid currentColor;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  from {
    transform: translate(-50%, -50%) rotate(0deg);
  }
  to {
    transform: translate(-50%, -50%) rotate(360deg);
  }
}

