import React, { useRef } from 'react';
import '../css/FileUploadCard.css';

const MAX_FILES = 5;
const SUPPORTED_EXT = new Set([
  'py', 'js', 'jsx', 'ts', 'tsx', 'java', 'kt', 'dart', 'go', 'rs', 'cpp', 'c', 'h', 'cs',
  'html', 'css', 'md', 'txt', 'json', 'yaml', 'yml',
  'pdf', 'doc', 'docx', 'xml'
]);

const FileUploadCard = ({ onUpload, fileInfo, isUploading, onError }) => {
  const fileRef = useRef(null);
  const [selectedNames, setSelectedNames] = React.useState([]);

  const filterSupported = (files) => {
    const supported = [];
    const unsupported = [];
    files.forEach((file) => {
      const ext = file.name.includes('.') file.name.split('.').pop().toLowerCase() : '';
      if (SUPPORTED_EXT.has(ext)) {
        supported.push(file);
      } else {
        unsupported.push(file.name);
      }
    });
    if (unsupported.length) {
      onError?.(`Unsupported files skipped: ${unsupported.join(', ')}`);
    }
    return supported;
  };

  const handleUploadClick = () => {
    const files = fileRef.current?.files;
    if (!files) return;
    const list = filterSupported(Array.from(files));
    if (list.length === 0) return;
    if (list.length > MAX_FILES) {
      onError?.(`Maximum ${MAX_FILES} files allowed You selected ${list.length}.`);
      return;
    }
    onUpload(list);
  };

  const handleFileChange = (e) => {
    const list = filterSupported(Array.from(e.target.files || []));
    setSelectedNames(list.map((f) => f.name));
    if (list.length > MAX_FILES) {
      onError?.(`Maximum ${MAX_FILES} files allowed You selected ${list.length}.`);
    }
  };

  const tooMany = selectedNames.length > MAX_FILES;
  const disabled = isUploading || selectedNames.length === 0 || tooMany;

  return (
    <div className="ctd-card">
      <h3>Upload document or code</h3>
      <input
        type="file"
        ref={fileRef}
        id="fileInput"
        accept=".py,.js,.jsx,.ts,.tsx,.java,.kt,.dart,.go,.rs,.cpp,.c,.h,.cs,.html,.css,.md,.txt,.json,.yaml,.yml,.pdf,.doc,.docx,.xml"
        multiple
        onChange={handleFileChange}
      />
      <div className="ctd-file-meta">
        <span className="ctd-muted">{selectedNames.length} / {MAX_FILES} files selected</span>
        {tooMany && <span className="ctd-error">Too many files selected</span>}
        {selectedNames.length > 0 && (
          <div className="ctd-file-list">
            {selectedNames.map((name) => (
              <div key={name} className="ctd-file-chip">{name}</div>
            ))}
          </div>
        )}
      </div>
      <button onClick={handleUploadClick} disabled={disabled}>
        {isUploading 'Uploading...' : 'Upload & Load'}
      </button>
      <div className="ctd-muted">{fileInfo}</div>
    </div>
  );
};

export default FileUploadCard;

import React, { useMemo } from 'react';
import '../css/OutputPanel.css';

const basicMarkdownToHtml = (text) => {
  if (!text) return '<p>(No content)</p>';
  const lines = text.split(/\r?\n/);
  const html = [];
  lines.forEach((line) => {
    const trimmed = line.trim();
    if (!trimmed) {
      html.push('');
      return;
    }
    if (/^###\s+/.test(trimmed)) {
      html.push(`<h3>${trimmed.replace(/^###\s+/, '')}</h3>`);
    } else if (/^##\s+/.test(trimmed)) {
      html.push(`<h2>${trimmed.replace(/^##\s+/, '')}</h2>`);
    } else if (/^#\s+/.test(trimmed)) {
      html.push(`<h1>${trimmed.replace(/^#\s+/, '')}</h1>`);
    } else if (/^(\d+\.|-|\*)\s+/.test(trimmed)) {
      // simple list handling
      const isOrdered = /^\d+\./.test(trimmed);
      const tag = isOrdered 'ol' : 'ul';
      const last = html[html.length - 1];
      if (!last || !last.startsWith(`<${tag}>`)) {
        html.push(`<${tag}>`);
      }
      html.push(`<li>${trimmed.replace(/^(\d+\.|-|\*)\s+/, '')}</li>`);
      const nextLine = lines[lines.indexOf(line) + 1];
      if (!nextLine || !/^(\d+\.|-|\*)\s+/.test(nextLine.trim())) {
        html.push(`</${tag}>`);
      }
    } else {
      html.push(`<p>${trimmed}</p>`);
    }
  });
  return html.join('\n');
};

const OutputPanel = ({ output, pdfLink, pdfInfo, summary }) => {
  const renderedHtml = useMemo(() => basicMarkdownToHtml(output), [output]);

  return (
    <div className="ctd-output-panel">
      <h2>Output</h2>
      <div className="ctd-doc-wrap">
        <div
          className="doc-preview"
          dangerouslySetInnerHTML={{ __html: renderedHtml }}
        />
      </div>
      <div className="ctd-actions">
        {pdfLink && (
          <a className="button link" href={pdfLink} download>
            Download PDF
          </a>
        )}
        {summary && <span className="ctd-muted">{summary}</span>}
        {pdfInfo && <span className="ctd-muted">{pdfInfo}</span>}
      </div>
    </div>
  );
};

export default OutputPanel;

import React, { useEffect, useState } from 'react';
import '../css/Toast.css';

const Toast = ({ message, type }) => {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    if (message) {
      setVisible(true);
      const timer = setTimeout(() => setVisible(false), 2400);
      return () => clearTimeout(timer);
    }
    return undefined;
  }, [message]);

  if (!message) return null;

  return (
    <div className={`toast ${visible 'show' : ''} ${type === 'error' 'error' : 'info'}`}>
      {message}
    </div>
  );
};

export default Toast;

