import { useCallback, useMemo, useRef, useState } from 'react';

const getApiBase = () => {
  if (typeof window === 'undefined') return 'http://localhost:5001';
  return window.location.origin.startsWith('http') window.location.origin
    : 'http://localhost:5001';
};

const useCode2Doc = () => {
  const apiBase = useMemo(getApiBase, []);
  const [fileInfo, setFileInfo] = useState('');
  const [title, setTitle] = useState('');
  const [contentType, setContentType] = useState('text');
  const [rawContent, setRawContent] = useState('');
  const [status, setStatus] = useState('');
  const [output, setOutput] = useState('Generated documentation will appear here.');
  const [pdfLink, setPdfLink] = useState('');
  const [pdfInfo, setPdfInfo] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [toast, setToast] = useState({ message: '', type: 'info' });
  const timersRef = useRef([]);
  const [summary, setSummary] = useState('');
  const [lastUploadMeta, setLastUploadMeta] = useState({ fileCount: null, contentType: null });

  const showToast = useCallback((message, type = 'info') => {
    setToast({ message, type });
    setTimeout(() => setToast({ message: '', type: 'info' }), 2800);
  }, []);

  const clearTimers = useCallback(() => {
    timersRef.current.forEach((t) => clearTimeout(t));
    timersRef.current = [];
  }, []);

  const handleUpload = useCallback(
    async (files) => {
      if (!files || files.length === 0) {
        showToast('Please choose file(s) first.', 'error');
        return;
      }

      const formData = new FormData();
      files.forEach((file) => formData.append('file', file));

      setIsUploading(true);
      setStatus('Uploading...');

      try {
        const res = await fetch(`${apiBase}/api/upload`, {
          method: 'POST',
          body: formData,
        });

        const data = await res.json();
        if (!res.ok) {
          throw new Error(data.error || 'Upload failed');
        }

        setRawContent(data.content || '');
        setContentType(data.content_type || 'text');
        setLastUploadMeta({
          fileCount: data.file_count || null,
          contentType: data.content_type || null,
        });
        setSummary('');

        const label = data.file_count `${data.file_count} file(s): ${data.filename}`
          : data.filename || 'files loaded';

        setFileInfo(`Loaded ${label} (${data.content_type})`);
        setStatus('File loaded You can edit the content before generating.');

        if (data.skipped && data.skipped.length) {
          showToast(`Skipped unsupported: ${data.skipped.join(', ')}`, 'error');
        } else {
          showToast('Upload ready', 'info');
        }
      } catch (err) {
        const message = err?.message || 'Upload error';
        setStatus('Upload failed');
        showToast(message, 'error');
      } finally {
        setIsUploading(false);
      }
    },
    [apiBase, showToast]
  );

  const handleGenerate = useCallback(async () => {
    const payload = {
      title: title.trim() || undefined,
      rawContent: rawContent.trim(),
      contentType,
      file_count: lastUploadMeta.fileCount || undefined,
    };

    if (!payload.rawContent) {
      showToast('Please add some content first.', 'error');
      return;
    }

    clearTimers();
    setIsGenerating(true);
    setStatus('Generating documentation...');
    setOutput('Generating document...');
    setPdfLink('');
    setPdfInfo('');
    setSummary('');

    const genId = Date.now();
    const draftTitle = payload.title || 'Untitled Document';

    const step1 = setTimeout(() => setOutput(`# ${draftTitle}`), 200);
    const step2 = setTimeout(
      () =>
        setOutput(
          `# ${draftTitle}\n\n## Table of Contents\n1 Overview\n2 Details\n3 Summary`
        ),
      500
    );
    const step3 = setTimeout(
      () =>
        setOutput(
          `# ${draftTitle}\n\n## Table of Contents\n1 Overview\n2 Details\n3 Summary\n\n## Overview\nDocument outline is being prepared...\n\n## Details\nCompiling key sections...`
        ),
      900
    );
    timersRef.current = [step1, step2, step3];

    try {
      const res = await fetch(`${apiBase}/api/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      const data = await res.json();
      if (!res.ok) {
        throw new Error(data.error || 'Generation failed');
      }

      clearTimers();
      const fileSummary = data.file_count || lastUploadMeta.fileCount;
      const typeSummary = data.content_type || lastUploadMeta.contentType || contentType;
      setSummary(
        `Generated from ${fileSummary `${fileSummary} file(s)` : 'N/A'}, type: ${typeSummary || 'text'}`
      );
      setOutput(data.docText || '');
      if (data.pdfUrl) {
        setPdfLink(data.pdfUrl);
        setPdfInfo(`PDF saved on server at uploads/${data.pdfFilename}`);
      }
      setStatus('Done.');
      showToast('Documentation generated', 'info');
    } catch (err) {
      const message = err?.message || 'Generation failed';
      setStatus('Generation failed');
      showToast(message, 'error');
    } finally {
      clearTimers();
      setIsGenerating(false);
    }
  }, [apiBase, clearTimers, contentType, lastUploadMeta, rawContent, showToast, title]);

  return {
    state: {
      fileInfo,
      title,
      contentType,
      rawContent,
      status,
      output,
      pdfLink,
      pdfInfo,
      isUploading,
      isGenerating,
      toast,
      summary,
    },
    actions: {
      setTitle,
      setContentType,
      setRawContent,
      handleUpload,
      handleGenerate,
      showToast,
    },
  };
};

export default useCode2Doc;

